package java_package;

public class Forloop_20 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
  for  ( int i= 20; i<=40;i++)
  {
	  if(i%2 ==0)
		{
			System.out.println("This is a even number "+i);
			
		}
		else
		{
			System.out.println("This is a odd number "+i);
		}
	
  }
}
}

